import React, { useState } from "react";

const ModelsPage = () => {
  const [formData, setFormData] = useState({
    state: "",
    district: "",
    season: "",
    crop: "",
    production: "",
    zn: "",
    fe: "",
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setPrediction(null);

    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setPrediction(data);
      } else {
        setError(data.error || "Server error");
      }
    } catch (err) {
      setError(err.message);
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-indigo-900 to-slate-900 p-6">
      <h1 className="text-4xl text-white mb-6">Crop Price Prediction</h1>
      <form onSubmit={handleSubmit} className="max-w-md space-y-4">
        <input
          name="state"
          placeholder="State"
          value={formData.state}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="district"
          placeholder="District"
          value={formData.district}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="season"
          placeholder="Season"
          value={formData.season}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="crop"
          placeholder="Crop"
          value={formData.crop}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="production"
          type="number"
          placeholder="Production (tonnes)"
          value={formData.production}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="zn"
          type="number"
          placeholder="Zinc (Zn)"
          value={formData.zn}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <input
          name="fe"
          type="number"
          placeholder="Iron (Fe)"
          value={formData.fe}
          onChange={handleChange}
          className="w-full p-2 rounded"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {loading ? "Predicting..." : "Predict"}
        </button>
      </form>

      {error && <p className="text-red-400 mt-4">{error}</p>}

      {prediction && (
        <div className="mt-6 text-white">
          <h2 className="text-2xl font-bold mb-2">Predicted Price</h2>
          <p>Per Quintal: ₹{prediction.predicted_price_quintal.toFixed(2)}</p>
          <p>Per Tonne: ₹{prediction.predicted_price_tonne.toFixed(2)}</p>
          <p>Per Kg: ₹{prediction.predicted_price_kg.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default ModelsPage;
