// src/pages/ModelsPage.js
import React, { useState } from "react";

const ModelsPage = () => {
  const [formData, setFormData] = useState({
    state: "",
    district: "",
    season: "",
    crop: "",
    production: "",
    zn: "",
    fe: "",
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setPrediction(null);

    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.error || "Server error");
      }

      const data = await response.json();
      setPrediction(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-indigo-900 to-slate-900 p-6 flex flex-col items-center">
      <h1 className="text-4xl font-bold text-white mb-6">Crop Price Prediction</h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white/10 backdrop-blur-lg rounded-xl p-6 w-full max-w-lg flex flex-col gap-4 border border-white/20"
      >
        {["state", "district", "season", "crop", "production", "zn", "fe"].map((field) => (
          <input
            key={field}
            type={field === "production" || field === "zn" || field === "fe" ? "number" : "text"}
            step="any"
            placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
            name={field}
            value={formData[field]}
            onChange={handleChange}
            className="p-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-400"
            required
          />
        ))}

        <button
          type="submit"
          className="mt-4 bg-purple-500 hover:bg-purple-600 text-white font-bold py-2 px-4 rounded-lg transition-colors"
        >
          {loading ? "Predicting..." : "Predict Price"}
        </button>
      </form>

      {error && (
        <div className="mt-4 text-red-400 font-semibold">
          Error: {error}
        </div>
      )}

      {prediction && (
        <div className="mt-6 bg-white/10 backdrop-blur-lg rounded-xl p-6 w-full max-w-md text-center border border-white/20">
          <h2 className="text-2xl font-bold text-white mb-4">Predicted Price</h2>
          <p className="text-white text-lg mb-2">
            Per Quintal: <span className="font-bold">₹{prediction.quintal}</span>
          </p>
          <p className="text-white text-lg">
            Per Tonne: <span className="font-bold">₹{prediction.tonne}</span>
          </p>
        </div>
      )}
    </div>
  );
};

export default ModelsPage;
